import React, { useCallback } from 'react';
import ReactDOM, { render } from 'react-dom';
import './index.css';

import mainLogo from './Tetris_T.png';

class Item extends React.Component{
  state = { xpos: 0 , ypos: 0 , xrel : 0 , yrel : 0 , drag: false ,
            pos3 : 0 , pos4 : 0 };

  move = (e) => {
    if(this.state.drag)
      this.setState(currentState => {
        return {xpos: e.clientX - currentState.xrel, 
          ypos: e.clientY - currentState.yrel};
      });
  };

  click = (e) => {
    this.setState(currentState => {
      return {
        xrel : e.clientX - currentState.xpos,
        yrel : e.clientY - currentState.ypos,
        drag : true
      };
    });
  };

  release = (e) => {
    this.setState(currentState => {
      return {
        drag : false
      };
    });
  };

  constructor(props){
    super(props);
    const FREE = 0;
    const DRAG = 1;

    //TODO: Add later if deemed necessary
    //document.addEventListener("mousemove", this.move, false);
  }

  elementDrag = (e) => {
    e = e || window.event;
    e.preventDefault();

    this.setState(currentState => {
      return {
        xpos : this.state.pos3 - e.clientX,
        ypos : this.state.pos4 - e.clientY,
        pos3 : e.clientX,
        pos4 : e.clientY,
      };
    });
  }

  closeDragElement = (e) => {
    document.onmouseup = null;
    document.onmousemove = null;
  }  

  dragMouseDown = (e) => {
    e = e || window.event;
    e.preventDefault();

    this.setState(currentState => {
      return {
        pos3 : e.clientX,
        pos4 : e.clientY,
      };
    });

    document.onmouseup = this.closeDragElement;
    document.onmousemove = this.elementDrag;
  }

  render(){
    return(
      <div>
        <img src={mainLogo}  
          alt="Tetris T Piece" 
          
          //onClick={this.click}
          //onmouseup={this.release}
          onMouseDown = {this.dragMouseDown}

          width="140" height="100"
          style={{
            position: "absolute",
            left: this.state.xpos + "px",
            top: this.state.ypos + "px",
          }}

          
        />

      </div>
    )
  }
}

class App extends React.Component{
    render() {
      return(
        <div>
          <Item />
        </div>
      )
    }
}

ReactDOM.render(
  <App />,
  document.getElementById('root')
);
