import React, { useCallback } from 'react';
import ReactDOM, { render } from 'react-dom';
import './index.css';

import tetris from './Tetris_T.png';
import githubicon from './github_icon.png';

var inDesc = false;
var menux = 0;

class Item extends React.Component{
  
  state = { xpos: 400 , ypos: 400 , drag: false ,
            pos1 : 0 , pos2 : 0,
            desc : "<h1>PLEASE ENTER DESCRIPTION</h1>",
            display_image : tetris };

  constructor(props){
    super(props);
  }

  // While dragging the element
  elementDrag = (e) => {
    
    if(!inDesc)
    {
      e = e || window.event;
      e.preventDefault();

      this.setState(currentState => {
        return {
          xpos : currentState.pos1 - currentState.xrel,
          ypos : currentState.pos2 - currentState.yrel,
          pos1 : e.clientX,
          pos2 : e.clientY,
        };
      });
    }
  }

  // When the user lets go of the mouse
  closeDragElement = (e) => {
    
    if(this.state.ypos < 200)
    {
      // If to be displayed
      if(!inDesc) 
      {
        this.setState(currentState => {
          return {
            xpos : 776,
            ypos : 92,
          };
        });
        inDesc = true;
        document.getElementById("dim").innerHTML = '<div class = "ashadow"></div><div class = "bshadow"></div>';
      }
    }
    else if(inDesc && this.state.ypos >= 200) 
    {
      inDesc = false;
    }
    document.onmouseup = null;
    document.onmousemove = null;
    
  }

  // When element is clicked
  dragMouseDown = (e) => {
    if(this.state.ypos < 200 && inDesc)
    {
      document.getElementById("dim").innerHTML = "";
      inDesc = false;
    }
    if(!inDesc)
    {
      e = e || window.event;
      e.preventDefault();

      this.setState(currentState => {
        return {
          xrel : e.clientX - currentState.xpos,
          yrel : e.clientY - currentState.ypos,
          pos1 : e.clientX,
          pos2 : e.clientY,
        };
      });

      document.onmouseup = this.closeDragElement;
      document.onmousemove = this.elementDrag;
    }
  }

  render(){

    let text;
    if(this.state.ypos < 200) {
      document.getElementById("description").innerHTML = this.state.desc;
    }
    

    return(
      <div id="item_description">
        <span id="description"></span>
        <img src={this.state.display_image}  
          alt="Tetris T Piece" 

          onMouseDown = {this.dragMouseDown}

          width="140" height="100"
          style={{
            position: "absolute",
            left: this.state.xpos + "px",
            top: this.state.ypos + "px",
          }}
        />
      </div>
    )
  }
}

class Tetris extends Item{
  constructor(props){
    super(props);
    this.state.desc = "<h1>Tetris Project - built with C++ and SFML</h1>";
  }
}

class GithubPiece extends Item{
  constructor(props){
    super(props);
    this.state.xpos = 700;
    this.state.ypos = 700;
    this.state.desc = '<h1>Github Account</h1>There are many projects that I have done <br>in the past! Check them out by<br> going to my github account!:<br><a href="https://github.com/lucidorangee">Click here to go to Jihoo\'s github link!</a>';
    this.state.display_image = githubicon;
  }
}

class OpenMenu extends Item{
  constructor(props){
    this.state.xpos = 900;
    this.state.ypos = 900;
  }
}

class Menu extends Item{
  constructor(props){
    super(props);
    this.state.desc = '<h1>MENU</h1>';
    this.state.xpos = 400;
    this.state.ypos = 600;
  }

  // When the user lets go of the mouse
  closeDragElement = (e) => {
    
    if(this.state.ypos < 200)
    {
      // If to be displayed
      if(!inDesc) 
      {
        menux = 0;
        this.setState(currentState => {
          return {
            xpos : 776,
            ypos : 92,
          };
        });
        inDesc = true;
        document.getElementById("dim").innerHTML = '<div class = "bshadow"></div>';
        document.getElementById("menu").innerHTML = '<div class = "sidenav" style="margin-left: ' + (0 - 160 + menux) +'px;";"><a href="#about">About</a><a href="#services">Services</a></div>';
        document.getElementById("content").className = "content";
      }
    }
    else if(inDesc && this.state.ypos >= 200) 
    {
      inDesc = false;
    }
    document.onmouseup = null;
    document.onmousemove = null;
    
  }

  // When element is clicked
  dragMouseDown = (e) => {
    if(this.state.ypos < 200 && inDesc)
    {
      document.getElementById("dim").innerHTML = "";
      document.getElementById("menu").innerHTML = '';
      document.getElementById("content").className = "contentnomenu";
      inDesc = false;
    }
    if(!inDesc)
    {
      e = e || window.event;
      e.preventDefault();

      this.setState(currentState => {
        return {
          xrel : e.clientX - currentState.xpos,
          yrel : e.clientY - currentState.ypos,
          pos1 : e.clientX,
          pos2 : e.clientY,
        };
      });

      document.onmouseup = this.closeDragElement;
      document.onmousemove = this.elementDrag;
    }
  }

  render(){

    let text;
    if(this.state.ypos < 200) {
      if(menux < 160)
      {
        menux = menux + 1;
      }
      document.getElementById("description").innerHTML = this.state.desc;
    }
    else 
    {
      if(menux > 0)
      {
        menux = menux - 1;
      }
    }
    

    return(
      <div id="item_description">
        <span id="description"></span>
        <img src={this.state.display_image}  
          alt="Tetris T Piece" 

          onMouseDown = {this.dragMouseDown}

          width="140" height="100"
          style={{
            position: "absolute",
            left: this.state.xpos + "px",
            top: this.state.ypos + "px",
          }}
        />
      </div>
    )
  }
}

class App extends React.Component{

    render() {
      
      return(
        <div className = 'container'>
          <span id="dim"></span>
          <span id="menu"></span>
          <div id='content' className = 'contentnomenu'>
            <Menu />
            <Tetris />
            <GithubPiece />
            <hr id="divide"></hr>
          </div>
          
        </div>
      )
    }
}

ReactDOM.render(
  
  <App />,
  document.getElementById('root')
);
