import React, { useCallback } from 'react';
import ReactDOM, { render } from 'react-dom';
import './index.css';

import mainLogo from './Tetris_T.png';

class Canvas extends React.Component{

  _onMouseMove(e){
    alert({x:e.nativeEvent.offsetX, y:e.nativeEvent.offsetY}.x);
  }
  
  render(){
    return(
      <div>
        <canvas 
          onMouseMove={
          (e) => this._onMouseMove(e)
        }
        />

        
      </div>
    )
  }
}

class Item extends Canvas{
  constructor(props){
    super(props);
    const FREE = 0;
    const DRAG = 1;

    this.state = FREE;
    this.xpos = 0;
    this.ypos = 0;

    function move(e){
      var newX = e.clientX - 10;
      var newY = e.clientY - 10;

      var image = this;
      this.xpos = newX + "px";
      this.ypos = newY + "px";
    }

    document.addEventListener("mousemove", move, false);
  }

  render(){

    return(
      <div>
        <img src={mainLogo}  
          alt="Tetris T Piece" 
          
          width="140" height="100"
          style={{
            position: "absolute",
            left: xpos + "px",
          }}
          
          onClick={
            () => {
              this.state=!this.state
              alert(this.state)
            }
          }

          
        />

      </div>
    )
  }
}

class App extends React.Component{
    render() {
      return(
        <div>
          <Canvas />
          <Item />
        </div>
      )
    }
}

ReactDOM.render(
  <App />,
  document.getElementById('root')
);
